import React, {Component} from 'react';
import { Link, Redirect } from 'react-router-dom';
import { Row, Col } from 'react-bootstrap';
import {admin} from '../firebase.js';
import Events from '../Eventdata.json';
import EventItem from './EventItem';

// Initialize firestore
let db = admin.firestore();
// Disable deprecated features
db.settings({
  timestampsInSnapshots: true
});

class EventsList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
      userEmail: ''
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    console.log(this.props.keyProp);
  }

  componentDidMount() {
    admin.auth().onAuthStateChanged((user) => {
      if(user === null) {
        this.setState({redirect: true});
      }
    });
  }

  render() {
    if(this.state.redirect === true) {
      return <Redirect to='/login' />
    }
    let eventLayout = Events.events;

    return (
      <div className="home-page">
        <h1 className="page-header">Live Events</h1>
        {
          eventLayout.map(function(user, i) {
            return <Col key={eventLayout[i].A} keyProp={eventLayout[i].A} onClick={(e) => this.handleClick(e)} className="home-col-ev" xs={2}>{eventLayout[i].value}</Col>
          }.bind(this))
        }
      </div>
    )
  }
}

export default EventsList;
