import React, { Component } from 'react';
import { Grid, Row, Col } from 'react-bootstrap';
import { Link, Redirect } from 'react-router-dom';
import {admin} from '../firebase.js';
import TableauReport from 'tableau-react';
import Modal from 'react-modal';

import Dashes from '../Dashboards.json'

// Initialize firestore
let db = admin.firestore();
// Disable deprecated features
db.settings({
  timestampsInSnapshots: true
});

class Dashboards extends Component {
    constructor(props) {
        super(props);

        this.state = {
          redirect: false,
          userEmail: '',
          modalIsOpen: false
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.resetEmail = this.resetEmail.bind(this);

        this.openModal = this.openModal.bind(this);
        this.afterOpenModal = this.afterOpenModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    handleChange(e) {
      let target = e.target;
      let value = target.type === 'checkbox' ? target.checked : target.value;
      let name = target.name;

      this.setState({
        [name]: value,
      });
    }

    componentDidMount(){
      admin.auth().onAuthStateChanged((user) => {
        // console.log(user.email);
        this.setState({userEmail: user.email});
        if(user === null) {
          this.setState({redirect: true});
        }
        var data = {
          name: user.displayName,
          email: user.email,
          dashboards: 'IT'
        };

        // Add a new document in collection "cities" with ID 'LA'
        var userRef = db.collection('users').doc(user.uid);

        var setWithOptions = userRef.set(data, {merge: true});

        // if the team of the individual is of a certain type, render a certain group of Dashboards
        var getDoc = userRef.get()
          .then(doc => {
            if (!doc.exists) {
              console.log('No such document!');
            } else {
              console.log('Document data:', doc.data().team);
              // While user's 'team' matches the doc, render it
              for (var i = 0; i < Dashes.dashboards.length; i++) {
                console.log(Dashes.dashboards[i].url);
                // if (Dashes.dashboards[i].group === doc.data().team) {
                //
                // }
              }
            }
          })
          .catch(err => {
            console.log('Error getting document', err);
          });
      });
    }

    // reset email
    resetEmail() {
      admin.auth().sendPasswordResetEmail(this.state.userEmail).then(function() {
        // Email sent.
        this.setState({modalIsOpen: true});
        console.log('modal state: ',this.state.modalIsOpen);
      }).catch(function(error) {
        // An error happened.
      });
      // console.log('useremail is: ', this.state.userEmail);
    }

    openModal() {
      this.setState({modalIsOpen: true});
    }

    afterOpenModal() {
      // references are now sync'd and can be accessed.
      this.subtitle.style.color = '#f00';
    }

    closeModal() {
      this.setState({modalIsOpen: false});
    }

    componentWillMount() {

    }
    componentWillUnmount() {

    }

    handleSubmit(e) {
      e.preventDefault();
      //on submit, set data in firebase equal to this new data
    }

    render() {
      if(this.state.redirect === true) {
        return <Redirect to='/login' />
      }
        return (
          <div>
            <h1 className="signin-page">Signed In</h1>
            <Modal
              isOpen={this.state.modalIsOpen}
              onAfterOpen={this.afterOpenModal}
              onRequestClose={this.closeModal}
              // style={customStyles}
              contentLabel="You will be receiving an password reset email shortly"
            >
              <h2 ref={subtitle => this.subtitle = subtitle}>Hello</h2>
              <button onClick={this.closeModal}>close</button>
              <div>I am a modal</div>
              <form>
                <input />
                <button>tab navigation</button>
                <button>stays</button>
                <button>inside</button>
                <button>the modal</button>
              </form>
            </Modal>
            <button onClick={this.resetEmail}>Reset Email</button>
            <div className="tableau-dashes">
              <TableauReport url='https://public.tableau.com/views/AgendaMedellnODS/Portada?:embed=y&:embed_code_version=3&:loadOrderID=0&:display_count=yes'/>
            </div>
          </div>
        );
    }
}
export default Dashboards;
